div h3+ul,
html.casper .post-full-content h3+ul,
div h3+ol,
html.casper .post-full-content h3+ol,
h3+.acc,
h4+.acc,
h5+.acc {
    margin-top: .85em
}

/* The switch - the box around the slider */
.acc-switch {
    position: relative;
    display: inline-block;
    width: 60px;
    height: 34px;
    zoom: 60%;
    margin: 2em 0em 0em 0em;
}

/* Hide default HTML checkbox */
.acc-switch input {
    opacity: 0;
    width: 0;
    height: 0;
}

/* The slider */
.acc-slider {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #ccc;
    -webkit-transition: .4s;
    transition: .4s;
}

.acc-slider:before {
    position: absolute;
    content: "";
    height: 26px;
    width: 26px;
    left: 4px;
    bottom: 4px;
    background-color: white;
    -webkit-transition: .4s;
    transition: .4s;
}

input:checked+.acc-slider {
    background-color: #14e687;
}


input:focus+.acc-slider {
    box-shadow: 0 0 1px #14e687;
}

input:checked+.acc-slider:before {
    -webkit-transform: translateX(26px);
    -ms-transform: translateX(26px);
    transform: translateX(26px);
}

/* Rounded sliders */
.acc-slider.round {
    border-radius: 34px;
}

.acc-slider.round:before {
    border-radius: 50%;
}

/* Accordion styles */
.acc {
    font-size: 90%;
    min-width: 1040px;
}

.acc.light-one {
    font-size: 100%
}

.acc.nwidth {
    min-width: auto;
}

.acc h1 {
    margin: 0;
    line-height: 2;
    text-align: center;
}

.acc h2 {
    margin: 0 0 0.5em;
    font-weight: normal;
}

.acc input {
    position: absolute;
    opacity: 0;
    z-index: -1;
}

.acc .row {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    margin-bottom: 1em;
}

.acc .row .col {
    /*flex: 0 24%;*/
    flex: 0 32%;
    /*flex: 1 1;*/
    /*flex: 0 45%;*/
}

.acc.threethird .row .col {
    flex: 0 75%;
}

.acc.full .row .col {
    flex: 0 100%;
}

.acc .row .col:not(:first-child) {}

.acc .tabs {
    border-radius: 4px;
    overflow: hidden;
    box-shadow: 0 4px 4px -2px rgba(0, 0, 0, 0.5);
}

.acc.light-one .tabs {
    box-shadow: none;
}

.acc .tab {
    width: 100%;
    color: white;
    overflow: hidden;
    border-bottom: .5px solid rgb(74, 74, 74);
}

.acc.light-one .tab {
    color: inherit;
    border-bottom: .5px solid #c5d2d9;
}

html.casper body.dark .acc.light-one .tab {
    color: whitesmoke;
    }

.acc.light-one .tab:last-child {
    border-bottom: none;
}

.acc .tab-label {
    display: flex;
    justify-content: space-between;
    padding: .35em .55em;
    background: rgba(117, 77, 238, 0.93);
    font-size: 90%;
    font-weight: normal;
    cursor: pointer;
    font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen, Ubuntu, Cantarell, Open Sans, Helvetica Neue, sans-serif;
}

.acc.light-one .tab-label {
    background: none;
    justify-content: left;
    font-size: 105%;
    font-weight: normal;
    padding: .85em .35em;
}

.acc .tab-label:hover {
    background: rgba(35, 38, 47, 0.93);
    -webkit-transition: background-color 200ms linear;
    -ms-transition: background-color 200ms linear;
    transition: background-color 200ms linear;
}

.acc.light-one .tab-label:hover {
    background: none;
}

.acc .tab-label::after {
    content: "\002B";
    width: 1em;
    height: auto;
    text-align: center;
    transition: all 0.35s;
    display: block;
}

.acc.light-one .tab-label::after {
    display: none;
}

.acc.light-one .tab-label::before {
    content: "\002B";
    width: 1em;
    height: auto;
    text-align: center;
    transition: all 0.35s;
    display: block;
    margin-right: .35em;
}

.acc .tab-content {
    max-height: 0;
    padding: 0 1em;
    color: rgba(44, 62, 80, 0.75);
    background: white;
    transition: all 0.35s;
    line-height: 1.45em;
    font-size: 90%;
    overflow: hidden;
}

.acc.light-one .tab-content {
    line-height: 1.755;
    font-size: 100%;
}

html.casper body.dark .acc.light-one .tab-content {
        color:whitesmoke;
    background:transparent;
    }

html.casper body.dark .acc .tab-content strong {
    color: rgba(44, 62, 80, 0.75);
}

html.casper body.dark .acc .tab-content a {
    color: rgba(44, 62, 80, 1) !important;
    box-shadow: inset 0 -1px 0 #27272b;
}

    html.casper body.dark .acc.light-one .tab-content a, html.casper body.dark .acc .tab-content strong {
    color: #fff !important;
box-shadow: inset 0 -1px 0 #fff;
}

.acc .tab-content>* {
    margin-bottom: .0em !important;
}

.acc.light-one .tab-content>* {
    margin-bottom: .0em !important;
    padding-left: 1.1em;
}

.acc .tab-close {
    display: flex;
    justify-content: flex-end;
    padding: 1em;
    font-size: 0.75em;
    background: rgba(10, 11, 11, 0.7);
    cursor: pointer;
}

.acc .tab-close:hover {
    background: rgba(35, 38, 47, 0.93);
}

.acc input:checked+.tab-label {
    background: rgba(35, 38, 47, 0.93);
}

.acc.light-one input:checked+.tab-label {
    background: white;
}

html.casper body.dark .acc.light-one input:checked+.tab-label {
    background: transparent;
}

.acc input:checked+.tab-label::after {
    content: "\2212";
    transform: rotate(360deg);
    -webkit-transform-origin: 50% 50%;
    -moz-transform-origin: 50% 50%;
    -o-transform-origin: 50% 50%;
    transform-origin: 50% 50%;
}

.acc.light-one input:checked+.tab-label::before {
    content: "\2212";
    transform: rotate(360deg);
    -webkit-transform-origin: 50% 50%;
    -moz-transform-origin: 50% 50%;
    -o-transform-origin: 50% 50%;
    transform-origin: 50% 50%;
}

.acc input:checked~.tab-content {
    max-height: 300vh;
    padding: 1em;
}

.acc.light-one input:checked~.tab-content {
    max-height: 300vh;
    padding: 1em;
    padding-bottom: 3em;
}

@media (max-width: 1040px) {
    .acc .row {
        display: flex;
        flex-wrap: wrap;
        justify-content: auto;
    }

    .acc .row .col {
        flex: 0 48.5%;
        margin-bottom: 1em;
    }

    .acc {
        font-size: 80%;
        min-width: auto;
    }

    .acc .row .col:not(:first-child) {
        margin-left: 0em;
    }

}
}

@media (max-width: 800px) {
    .acc {
        font-size: 100%;
        min-width: auto;
    }

    .acc .row {
        display: flex;
        flex-flow: column wrap;
        align-content: space-between;
    }

    .acc .row .col:not(:first-child) {
        margin-left: 0;
    }

    .acc.light-one .tab-content>* {
        margin-bottom: .0em !important;
        padding-left: none;
    }
}
